import { Directive, ElementRef, Input, Renderer2 } from "@angular/core";

@Directive({
  selector: "[abcDir]",
})
export class ABCDirective {
  @Input() abcDir: number = 0;
  @Input() dec: number = 3;

  constructor(private elm: ElementRef, private renderer: Renderer2) {}

  ngOnInit(): void {
    this.check(this.abcDir);
  }

  private check(index: number): void {
    if (index % this.dec) {
      this.renderer.setStyle(this.elm.nativeElement, "color", "red");
    }
  }
}
